import axios from 'axios';
import React, { Component } from 'react'

class SignUp extends Component {

  state = {
    email : '',
    pw : '',
    pw_confirm : ''
  }

  onChangeHandler = (e)=>{
      e.preventDefault();    
      this.setState({
      [e.target.name] : e.target.value
    })
  }

  onSubmitHandler = (e)=>{
    e.preventDefault();
    const {email , pw, pw_confirm} = this.state;
    if(pw !== pw_confirm) {
      return alert('비밀번호가 일치하지 않습니다.');
    }
    if(pw.length<8) {
      return alert('비밀번호를 8자리 이상으로 설정해 주세요')
    }

    axios({//
      method : 'post',
      url : 'server/signup',//수정 필요
      data : {
        email : email,
        password : pw
      }
    })
    .then(function(res) {
      console.log(res);
    })
    .catch(function (err) {
      console.log(err);
    })
  }

  render() {
    return(
      <div>
        <h1>회원가입</h1>
        <form
          onSubmit={(e)=>{this.onSubmitHandler(e)}}
        >
          <label>email</label>
          <input 
            type="email" 
            name="email"
            value={this.state.email}
            onChange={(e)=>{this.onChangeHandler(e)}}
          />
          <label>password</label>
          <input 
            type="password" 
            name="pw"
            value={this.state.pw}
            onChange={(e)=>{this.onChangeHandler(e)}}
          />
          <label>password confirm</label>
          <input 
            type="password" 
            name="pw_confirm" 
            value={this.state.pw_confirm}
            onChange={(e)=>{this.onChangeHandler(e)}}
          />
          <input type="submit" value="제출" />
        </form>

      </div>
    );
  }
}

export default SignUp;
